USE `pgestao_app`;
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (1, 'Tecnoclogia da informação e comunica', 'O departamento de tecnologia da informação e comunicação congrega os segmentos Telecom, Hardwares de informática e Software e Serviços de TI. Responsável por sustentar a parte tecnológica da empresa e planejar os investimento em tecnologia. ', 1);
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (2, 'Financeiro', 'O departamento financeiro é aquele que administra os recursos de uma empresa. Ele faz o controle da tesouraria, dos investimentos e dos riscos, além da gestão de contas e impostos, do planejamento financeiro da companhia e da divulgação de seus resultados', 1);
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (3, 'Administrativo', 'O setor administrativo é o coração da empresa. Ele é responsável pelo planejamento estratégico e pela gestão das tarefas, coordenando e fiscalizando os demais setores e fornecendo os dados necessários para a tomada de decisões pelos executivos da companhi', 1);
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (4, 'Recursos humanos', 'O departamento de recursos humanos é o responsável pelo recrutamento do pessoal e pela gestão de pessoas. Ele busca soluções para conflitos, controla os horários de entrada e saída dos trabalhadores, as horas extras e estabelece políticas para a retenção ', 1);
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (5, 'Comercial', 'Também chamado de departamento de marketing, o foco do setor comercial é nos clientes externos da empresa. Ele é o responsável pelas vendas, garantindo a geração de receitas para a empresa.', 1);
INSERT INTO `pgestao_app`.`department` (`id`, `name`, `description`, `company`) VALUES (6, 'Operacional', 'O departamento operacional, que em alguns casos pode ser chamado de setor de produção ou de setor técnico, é o responsável por administrar todo o processo de transformação dos insumos no produto final.', 1);
