START TRANSACTION;
USE `pgestao_app`;
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (1, 'Auxiliar de Faturamento', 'Calcula a movimentação diária do faturamento, encaminhando aos setores competentes, conforme normas e procedimentos da empresa ; auxilia nos controles de documentos (pedidos) que dão entrada para sejam feitos os respectivos faturamento; preenche minutas, c', 2);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (2, 'Encarregado de Recursos Humanos', 'Está sob as responsabilidades de um Encarregado de Recursos Humanos manter o plano de cargos, salários e carreira da empresa de acordo com as normas da instituição, prestar informações aos funcionários da instituição sobre procedimentos do setor de recurso', 4);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (3, 'Analista Comercial Interno ', 'Está sob as responsabilidades de um Analista Comercial Interno realizar a atualização de cadastros de clientes, fazer tratamento e arquivamento de documentação, analisar o volume de faturamento, assim como a análise de processos de venda , cotações, propos', 5);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (4, 'Gerente Administrativo', 'Um Gerente Administrativo atua com a gestão da equipe, respondendo pelos recursos humanos, supervisionando o setor de compras dando assessoria a presidência da empresa, elaborando relatórios gerenciais e conduzir reuniões de recursos materiais e financeiro', 3);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (5, 'Coordenador de Infraestrutura', 'Administra as rotinas de manutenção, implantação e configuração de infraestrutura de telecomunicações, redes, servidores e internet, acompanha o suporte aos usuários e desenvolve soluções de tecnologia, a fim de manter a alta disponibilidade dos serviços.', 1);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (6, 'Eletricista de Manutenção ', 'Está sob as responsabilidades de um Eletricista de Manutenção executar manutenção elétrica, preventiva e corretiva, a fim de manter máquinas, equipamentos, motores, painéis, rede elétrica, aparelhos e instalações em perfeitas condições de funcionamento, at', 6);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (7, 'Fresador', 'Está sob as responsabilidades de um Fresador operar máquina de fresa, documentar suas atividades e manter os formulários, como os de produção e de ocorrência de manutenção das máquinas, sempre atualizados, obter diferentes cortes nos materiais, abrir fenda', 6);
INSERT INTO `pgestao_app`.`role` (`id`, `name`, `description`, `department`) VALUES (8, 'Fundidor', 'Está sob as responsabilidades de um Fundidor atuar com fabricação e fundição de medalhas, atuar com vulcanização e fundição, dar acabamento em peças de metais, fundir peças em moldes em conquilhas, fazer a reparação das peças, fazer a verificação e organiz', 6);

COMMIT;


